"use strict"

const process = require("process")

const defaultOptions = envName => {
  return {
    presets: [
      "/data/wuqiaoqi/avatest/node_modules/babel-preset-es2015/lib/index.js",
      "/data/wuqiaoqi/avatest/node_modules/babel-preset-stage-0/lib/index.js",
      "/data/wuqiaoqi/avatest/node_modules/babel-preset-react/lib/index.js"
    ],
    babelrc: false,
    env: {
      [envName]: {
        plugins: [],
        presets: [
          [
            "/data/wuqiaoqi/avatest/node_modules/@ava/babel-preset-transform-test-files/index.js",
            {
              powerAssert: true
            }
          ]
        ]
      }
    }
  }
}

const envOptions = Object.create(null)

exports.getOptions = () => {
  const envName = process.env.BABEL_ENV || process.env.NODE_ENV || "development"
  return envName in envOptions
    ? envOptions[envName]()
    : defaultOptions(envName)
}
